var inputs = [
  [10, 20, 30, 100, 12345],
  [0, 0, 0, 0, 0, 20, 30, 40, 50, 100],
  [-50, -25, 0, 10, 23, 34, 45, 56, 77, 111],
  [-11, 0, 25, 50, 124],
  [-11, 0, 25, 50, 60, 70, 80, 124],
  [10, 10, 10, 15, 20, 34, 45, 80],
  [0],
  [10],
  [0, 10],
  [1, 10],
  [10, 20],
  [-1, 10, 20],
  [1, 10, 100]
];

var results = [
  { '1' : 15, '2' : 30, '3' : 6222.5 },
  { '1' : 0, '2' : 10, '3' : 45 },
  { '1' : 0, '2' : 28.5, '3' : 66.5 },
  { '1' : -5.5, '2' : 25, '3' : 87 },
  { '1' : 12.5, '2' : 55, '3' : 80 },
  { '1' : 10, '2' : 17.5, '3' : 45 },
  { '1' : 0, '2' : 0, '3' : 0 },
  { '1' : 10, '2' : 10, '3' : 10 },
  { '1' : 0, '2' : 5, '3' : 10 },
  { '1' : 1, '2' : 5.5, '3' : 10 },
  { '1' : 10, '2' : 15, '3' : 20 },
  { '1' : -1, '2' : 10, '3' : 20 },
  { '1' : 1, '2' : 10, '3' : 100 }
];

test( "utils quartiles", function() {
  var i = 0;
  var str = '';
  while (i < inputs.length)
  {
    for (var j = 1 ; j < 4 ; j ++)
    {
      str = 'Q' + j + ': ' + JSON.stringify(inputs[i]);
      ok( quartile(inputs[i],j) == results[i][j], str);
    }
    i ++;
  }
});
